import React, { useState, useEffect } from "react";
import { connect } from "react-redux";
import { setCoinsThunkCreator } from "../../redux/coin-reducer";
import CoinBlock from "./coinElement";
const CoinElement = (props) => {
  const [currentPage, setCurrentPage] = useState(1);
  const [itemsPerPage, setItemsPerPage] = useState(100);

  useEffect(() => {
    props.setCoinsThunkCreator();
  }, []);

  console.log(props.coins);

  const indexOfLastItem = currentPage * itemsPerPage;
  const indexOfFirstItem = indexOfLastItem - itemsPerPage;
  const currentItems = props.coins.slice(indexOfFirstItem, indexOfLastItem);

  const CoinBlockElem = currentItems.map((el) => (
    <CoinBlock
      name={el.name}
      img={el.logo_url}
      price={el.price}
      price_change_prc={
        el["1d"] !== undefined
          ? el["1d"].price_change_pct
          : el["7d"].price_change_pct
      }
      market_cap={el.market_cap ? el.market_cap : NaN}
    />
  ));

  return (
    <div>
      <div>
        <img src={props.img} />
      </div>
      <div>
        <div>{props.name}</div>
      </div>
      <div>
        <div>${+props.price}</div>
      </div>
      <div>
        <div>{+props.price_change_prc}</div>
      </div>
      <div></div>
    </div>
  );
};

const mapStateToProps = (state) => {
  return {
    coins: state.coinsPage.coins,
  };
};
const CoinContainer1 = connect(mapStateToProps, { setCoinsThunkCreator })(
  CoinElement
);

export default CoinContainer1;
