import React, { useEffect, useState } from "react";
import { currencies, currenciesPerPage } from "../../API/api";
import CoinBlock from "./coinElement";
import s from "./CoinElement.module.css";

const CoinElementContainer = () => {
  //debugger
  const [coins, setCoins] = useState([]);
  const [totalCoins, setTotalCoins] = useState([]);
  const [pages, setPage] = useState([]);
  const [currentPage, setCurrentPage] = useState(1);
  const [itemsPerPage, setItemsPerPage] = useState(100);
  const [pageNumberLimit, setPageNumberLimit] = useState(5);
  const [minPageNumberLimit, setMinPageNumberLimit] = useState(0);
  const [maxPageNumberLimit, setMaxPageNumberLimit] = useState(5);
  const [isFetching, setIsFetching] = useState(true)

  useEffect(() => {
    return currenciesPerPage(currentPage)
      .then((res) => {
        setCoins(res.data);
        console.log(coins)
        setIsFetching(false)
      })
      .catch((error) => {
        console.log(error);
      });
  }, [currentPage]);

  useEffect(() => {
    //debugger
    return currencies()
      .then((res) => {
        setTotalCoins(res.data);
      })
      .catch((error) => {
        console.log(error);
      });
  }, [coins]);

  //useEffect(() => {
  //  setPage(totalCoins);
  //}, []);

  const indexOfLastItem = currentPage * itemsPerPage;
  const indexOfFirstItem = indexOfLastItem - itemsPerPage;
  const currentItems = pages.slice(indexOfFirstItem, indexOfLastItem);

  

  const amountOfPages = [];

  for (let i = 1; i < Math.ceil(totalCoins.length / 100); i++) {
    amountOfPages.push(i);
  }

  const currentPageFunc = (el) => {
    setCurrentPage(+el.target.id);
  };
 

  let PaginationEl = amountOfPages.map((el) => {
    if (el > minPageNumberLimit && el <= maxPageNumberLimit) {
      return (
        <li
          key={el}
          name={el}
          id={el}
          onClick={currentPageFunc}
          className={(currentPage === el) && (el > 0) ? s.activeEl : null}
        >
          {el}
        </li>
      );
    }
  });

  const handleNext = () => {
    setCurrentPage(currentPage + 1);
    console.log(currentPage)
    if (currentPage +1  > maxPageNumberLimit) {
      setMaxPageNumberLimit(maxPageNumberLimit + pageNumberLimit);
      setMinPageNumberLimit(minPageNumberLimit + pageNumberLimit);
    }
  };


  const handlePrev = () => {
    if(currentPage > 1){
      setCurrentPage(currentPage - 1);
    }
    console.log(currentPage);
    console.log(minPageNumberLimit);
    if (currentPage - 1 <= minPageNumberLimit && currentPage !== 1) {
      setMaxPageNumberLimit(maxPageNumberLimit - pageNumberLimit);
      setMinPageNumberLimit(minPageNumberLimit - pageNumberLimit);
    }
  };

  const handleBack = () => {
    setCurrentPage(currentPage - pageNumberLimit)
    setMaxPageNumberLimit(maxPageNumberLimit - pageNumberLimit);
    setMinPageNumberLimit(minPageNumberLimit - pageNumberLimit);
  }

  const handleFuther = () => {
    setCurrentPage(currentPage + pageNumberLimit)
    setMaxPageNumberLimit(maxPageNumberLimit + pageNumberLimit);
    setMinPageNumberLimit(minPageNumberLimit + pageNumberLimit);
  }

  const CoinBlockElem = coins.map((el) => {
    <CoinBlock
      name={el.name}
      img={el.logo_url}
      price={el.price}
      price_change_prc={el["1d"].price_change_pct}
      market_cap={el.market_cap}
    />
  });
  return (
    
    <div>
      {isFetching ? 'loading...' : CoinBlockElem }
        <ul className={s.pagination}>
          <li>
            <button onClick={handlePrev}>Prev</button>
          </li>
          {minPageNumberLimit >= pageNumberLimit ?
          <li>
          <button onClick={handleBack}>...</button>
        </li> : null}
          {PaginationEl}
          <li>
            <button onClick={handleFuther}>...</button>
          </li>
          <li>
            <button onClick={handleNext}>Next</button>
          </li>
        </ul> 
    </div>

  );
};

export default CoinElementContainer;
