import { currencies } from "../API/api"

const SORTCOINS = 'SORT_COINS'
const SETCOINS = 'SET_COINS'

let initState ={
    coins:[]
}

const coinReducer = (state = initState, action) => {
    switch (action.type){
        case SETCOINS:
            return{
                ...state,
                coins: action.coins
            }
        case SORTCOINS:
            return{
                ...state
            }
        default:
            return state
    }
}


export const setCoinsAC = (coins) => {
    return {
        type: SETCOINS,
        coins
    }
}

export const setCoinsThunkCreator = () => async (dispatch) => {
    debugger
    const response = await currencies().then(res => {
        setCoinsAC(res.data);
    })
    return response
}

export default coinReducer
